function slideshow(){let e=0,t=document.getElementById("slideshow-carousel").children;for(let s=0;s<t.length;s++)t[s].addEventListener("transitionend",()=>{t[s].classList.remove("left"),t[s].classList.remove("right"),e===s?t[s].classList.add("active"):t[s].classList.remove("active")});setInterval(function(){for(let s=0;s<t.length;s++)e===s&&(t[s].classList.add("left"),s===t.length-1?t[0].classList.add("right"):t[s+1].classList.add("right"));e===t.length-1?e=0:e++},5e3)}function close_side_menu(){document.getElementById("side_menu_checkbox").checked=!1}window.addEventListener("scroll",function(e){window.scrollY>=70?document.getElementById("body").classList.add("scroll"):window.scrollY<70&&document.getElementById("body").classList.remove("scroll")}),window.onload=function(){AOS.init(),slideshow()};